INSERT INTO `uek_acdemic_indicators` VALUES ('40', '40', '指标点3-2 非技术因素：能够在社会、健康、安全、法律、文化以及环境等因素约束下，对设计方案的可行性进行分析。', '0.2', '非技术因素');
