INSERT INTO `uek_acdemic_indicators` VALUES ('44', '44', '指标点4-3 实验结果分析：能正确采集、整理、综合实验数据及相关信息，对多因素实验结果进行关联处理，并对误差来源和影响进行综合分析，得到有效结论。', '0.3', '实验结果分析');
