INSERT INTO `uek_evaluate_titles` VALUES ('645', '01020301016007', '如何理解包装集?', '6', '1', 'Query包装集指的是通过$()方法返回的一个元素集，这跟一般的javascript数组有所区别， 包装集在后者的基础上还有一些初始化的函数和属性。', '', '8', '2016-12-17 16:31:04', '3', null, '0', '1', '2', '13', '36', null, null, '1', null);
INSERT INTO `uek_evaluate_titles` VALUES ('646', '01020301016008', 'jquery的each、map、hasJquery的属性操作', '6', '3', 'map()方法主要用来遍历操作数组和对象，map()方法会返回一个新的数组。each()主要用于遍历jquery对象。each()返回的是原来的数组，并不会新创建一个数组。\nhas() 方法返回拥有匹配指定选择器的一个或多个元素在其内的所有元素。', '', '8', '2016-12-17 16:31:04', '5', null, '0', '1', '2', '13', '36', null, null, '1', null);
