INSERT INTO `uek_evaluate_titles` VALUES ('662', '01020301016024', 'jQuery解决不同类库冲突的方法是什么？', '6', '1', '方法一：为jQuery自定义命名空间，使得jQuery不使用其默认的名字空间，比如使用$j()而不是jQuery()。                                                             方法二：重载$函数。使用jQuery.noConflict()就可以通过重载$函数，从而区分开跟其他js库的重叠部分。', '', '8', '2016-12-17 16:31:04', '10', null, '0', '1', '2', '13', '38', null, null, '1', null);
INSERT INTO `uek_evaluate_titles` VALUES ('663', '01020301016025', '列举几个jquery常用工具函数。', '6', null, '1.$.each(object, [callback]) 循环遍历对象  2.$.grep(array, fn, [invert]) 查找元素  3.$.makeArray(obj) 对象变为数组 4.$.map(array, callback) 遍历元素，修改元素 5. $.trim(str) 清除前后空格  6.$.merge(first, second) 合并两个数组', '', '8', '2016-12-17 16:31:04', '10', null, '0', '1', '2', '13', '38', null, null, '1', null);
