INSERT INTO `uek_evaluate_titles` VALUES ('722', '01020301064001', '利用过滤选择器实现隔行换色效果', '4', '', '&lt;!DOCTYPE&gt;\n&lt;html&gt;\n    &lt;head&gt;\n        &lt;title&gt;隔行换色&lt;/title&gt;\n        &lt;meta charset=&quot;utf-8&quot; /&gt;\n        \n        &lt;script type=&quot;text/javascript&quot; src=&quot;../jquery-1.4.4.js&quot;&gt;&lt;/script&gt;\n        &lt;script type=&quot;text/javascript&quot;&gt;\n        //页面加载，设置行的颜色\n        window.onload = function(){\n            $(&quot;tr:first&quot;).css(\'background-color\',\'lightgreen\');\n            //偶数除去第一个\n            //$(&quot;tr:even:not(:first)&quot;).css(\'background-color\',\'lightblue\');\n            //:gt()大于给定索引的值开始，从0开始\n            $(&quot;tr:gt(0):odd&quot;).css(\'background-color\',\'lightblue\');\n        }\n        &lt;/script&gt;\n        &lt;style type=&quot;text/css&quot;&gt;\n        table {\n   width:500px;\n   border:1px solid black;\n   margin:auto; \n  }\n        td {\n   border:1px solid black;\n  }\n        &lt;/style&gt;\n    &lt;/head&gt;\n    &lt;body&gt;\n       \n        &lt;table style=&quot;height:100%;width:100%&quot;&gt;\n   &lt;tr&gt;\n    &lt;td&gt;序号&lt;/td&gt;&lt;td&gt;姓名&lt;/td&gt;&lt;td&gt;年龄&lt;/td&gt;&lt;td&gt;性别&lt;/td&gt;\n   &lt;/tr&gt;\n   &lt;tr&gt;\n    &lt;td&gt;1&lt;/td&gt;&lt;td&gt;王小二&lt;/td&gt;&lt;td&gt;12&lt;/td&gt;&lt;td&gt;男&lt;/td&gt;\n   &lt;/tr&gt;\n   &lt;tr&gt;\n    &lt;td&gt;2&lt;/td&gt;&lt;td&gt;张三&lt;/td&gt;&lt;td&gt;44&lt;/td&gt;&lt;td&gt;男&lt;/td&gt;\n   &lt;/tr&gt;\n   &lt;tr&gt;\n    &lt;td&gt;3&lt;/td&gt;&lt;td&gt;李四&lt;/td&gt;&lt;td&gt;23&lt;/td&gt;&lt;td&gt;女&lt;/td&gt;\n   &lt;/tr&gt;\n   &lt;tr&gt;\n    &lt;td&gt;4&lt;/td&gt;&lt;td&gt;王五&lt;/td&gt;&lt;td&gt;35&lt;/td&gt;&lt;td&gt;男&lt;/td&gt;\n   &lt;/tr&gt;\n   &lt;tr&gt;\n    &lt;td&gt;5&lt;/td&gt;&lt;td&gt;赵六&lt;/td&gt;&lt;td&gt;26&lt;/td&gt;&lt;td&gt;男&lt;/td&gt;\n   &lt;/tr&gt;\n        &lt;/table&gt;\n    &lt;/body&gt;\n&lt;/html&gt;', '', '10', '2016-12-17 16:32:12', '10', '0',