INSERT INTO `uek_evaluate_titles` VALUES ('1235', '01010701016011', '简述一下项目开发流程。', '6', '1', '软件开发正规流程\r\n\r\n1 可行性分析\r\n	经济可行性\r\n	技术可行性\r\n\r\n2 需求分析 ---------------- 需求分析师\r\n	分析要开发的系统要具备什么功能\r\n\r\n3 设计-----------------------------项目经理或架构师\r\n	概要设计\r\n		设计这个系统 项目的原型 （一堆html 专门的工具快速出原型  可以拿原型和客户核对 避免做了很多工作 然后发现不是客户要的东西）\r\n		可以分析出来这个项目有哪些大的模块划分\r\n\r\n		产出物：概要设计说明书\r\n\r\n	详细设计\r\n		在概要设计的基础上，继续细化，设计要多少个class、每个class有多少个method，每个method参数和返回值类型。\r\n		\r\n		产出物：详细设计说明书\r\n\r\n数据库设计	------------------数据库dba	\r\n		就是设计这个系统 项目的数据库，具体是要多少张表，每个表多少个字段，每个字段有什么约束，表与表之间什么关系？\r\n\r\n4 编码--------------------------------------软件开发工程师|前端|后台\r\n		团队合作 svn\r\n\r\n5 测试--------------------------------------软件测试工程师（技术要求懂一些也不是那么强 严谨 细心 很多计科女生适合 最好先从开发出身 发展潜力更大）\r\n	单元测试、集成测试 黑盒测试|白盒测试 压力测试	\r\n\r\n6 安装部署运维------------------------------------运维工程师（linux 安装 配置 调试 各类数据库安装 配置 调试）\r\n	\r\n7 二次开发\r\n\r\n在整个软件开发的过程当中， 时间划分编码只占3/10的时间，所以认为软件就是编程是错的\r\n', '流程的几个步骤', null, '2017-08-24 08:27:25', '10', '5', '0', '1', '1', '7', '13', null, null, '1', null);
