INSERT INTO `uek_evaluate_answer` VALUES ('2719', '262', '369', '867', 'CSS3是CSS技术的升级版本，CSS即层叠样式表（Cascading StyleSheet）。 在网页制作时采用层叠样式表技术，可以有效地对页面的布局、字体、颜色、背景和其它效果实现更加精确的控制。 只要对相应的代码做一些简单的修改，就可以改变同一页面的不同部分，或者页数不同的网页的外观和格式。', '0', '2017-08-01 09:01:50', null, '');
