INSERT INTO `uek_evaluate_answer` VALUES ('2799', '268', '375', '874', '重载是让类以统一的方式处理不同类型数据的一种手段。多个同名函数同时存在，具有不同的参数个数/类型。\r\n\r\n\r\n 父类与子类之间的多态性，对父类的函数进行重新定义。如果在子类中定义某方法与其父类有相同的名称和参数，我们说该方法被重写 (Overriding)。', '0', '2017-08-03 08:38:17', null, '');
