INSERT INTO `uek_evaluate_answer` VALUES ('4456', '328', '422', '1240', '&lt;form action=&quot;register.php&quot;&gt;\r\n\r\n用户名 &lt;input type=&quot;text&quot; name=&quot;name&quot;&gt;&lt;br&gt;\r\n密&amp;nbsp;&amp;nbsp;码&lt;input type=&quot;password&quot; name=&quot;password&quot; &gt;&lt;br&gt;\r\n邮&amp;nbsp;&amp;nbsp;箱&lt;input type=&quot;text&quot; name=&quot;email&quot; &gt;&lt;br&gt;\r\n&lt;input type=&quot;submit&quot; value=&quot;注册&quot; &gt;\r\n\r\n&lt;form&gt;\r\n&lt;hr&gt;\r\n\r\n&lt;?php\r\n\r\n\r\n	//1 接收客户端的输入\r\n		\r\n	$name = $_GET[\'name\'];\r\n	$password = $_GET[\'password\'];\r\n	$email = $_GET[\'email\'];\r\n\r\n	//2 连接数据库 拼接sql语句 发送sql语句到对面的数据库执行 关闭数据库\r\n	\r\n	//2.1 连接数据库\r\n	$link = mysqli_connect(\'localhost\', \'root\' , \'root\' , \'yh_shop\');\r\n	\r\n	//2.2 拼接sql语句\r\n\r\n	$sql = &quot;insert into t_member(name,password,email) values(\'name\',\'$password\' , \'$email\')&quot;;\r\n\r\n	//2.3 发送sql语句到对面的mysq数据库执行\r\n	mysqli_query($link , $sql);	\r\n	\r\n	//2.4 关闭数据库\r\n\r\n	mysqli_close($link);//过河拆桥\r\n\r\n', '0', '2017-08-25 08:46:14', null, '');
