INSERT INTO `uek_evaluate_answer` VALUES ('4861', '269', '429', '1195', 'ajax是一种用于创建快速动态网页的技术，通过在后台与服务器进行少量数据交换，ajax可以实习网页异步更新，可以在不加载整个网页的情况下是网页中的部分内容进行更新。', '0', '2017-08-30 08:32:08', null, '');
