INSERT INTO `uek_evaluate_answer` VALUES ('5186', '286', '442', '1254', '推广目标的推广思路：蓄势期：培养基础销量。预热期：提高店铺、宝贝收藏，提升人气，抢占用户注意力\r\n推广目标的推广方向：成交订单\r\n定向策略的推广思路：蓄势期：定向拉新人群，培养认知用户。预热期：将潜在用户转化为认知用户，提高店铺、宝贝收藏\r\n定向策略的推广方向：认知用户对店铺、宝贝的收藏、加购、浏览、搜索店铺\r\n展位策略的推广思路：蓄势期：测试为主。预热期：增加进店的UV\r\n展位策略的推广方向：锁定站内资源位进行投放，如果预算充足，可补充部分站外资源位', '0', '2017-09-06 08:52:07', null, '');
