INSERT INTO `uek_evaluate_answer` VALUES ('5192', '281', '442', '1254', '推广目标：蓄势期：对产品测试，图片测试，产品用户\r\n          预热期：积累用户，提升关注度\r\n定向策略：蓄势期：定向沉默用户和购买用户达到拉新目   的和提高测试的精准度\r\n          预热期：确定拉新用户和转化用户，有针对性进行投放提升用户认知（提高点击，收藏，加购，购买）\r\n展位策略：蓄势期：主要进行站内大流量资源位\r\n          预热期：除站内资源外，预算充足可以进行站外推广', '0', '2017-09-06 08:59:28', null, '');
