INSERT INTO `uek_evaluate_answer` VALUES ('5368', '268', '423', '135', 'echo（）一般用来输出简单的变量、数值类型的文本。它是语法结构，不是函数。可以同时打印多个值。\r\nprint（） 和echo（）比较类似，不同的是print是函数，有返回值。而echo没返回值。\r\nprint_r（） 可以打印字符串和数值的形式，有返回值。如果是空，输出null；而echo（）则不显示。', '0', '2017-09-09 17:44:45', null, '');
