INSERT INTO `uek_evaluate_answer` VALUES ('5381', '271', '423', '135', 'echo是PHP语句, print和print_r都是函数,语句没有返回值,函数可以有返回值\r\nprint（）只能打印出简单类型变量的值\r\nprint_r（）可以打印出复杂类型变量的值，比如数组\r\necho 输出一个或多个字符串\r\n', '0', '2017-09-09 18:17:26', null, '');
