INSERT INTO `uek_evaluate_answer` VALUES ('5384', '271', '423', '147', 'PHP是一个基于服务端来创建动态网站的脚本语言，可以用PHP和HTML生成网站主页，进行web开发。\r\nPHP:是超文本预处理程序。PHP是运行在服务器端的解释性脚本语言。\r\n', '0', '2017-09-09 18:17:26', null, '');
