INSERT INTO `uek_evaluate_answer` VALUES ('5387', '271', '423', '150', 'php中单引号可以解析部分转义字符，双引号用于解析全部转义字符，在双引号中不能嵌套双引号，同时单引号也不能嵌套单引号，但是单引号中可以嵌套双引号，双引号中可以嵌套单引号。', '0', '2017-09-09 18:17:26', null, '');
