INSERT INTO `uek_evaluate_answer` VALUES ('7326', '269', '479', '1349', '动态页面:\r\n&lt;!DOCTYPE html&gt;\r\n&lt;html lang=&quot;en&quot;&gt;\r\n&lt;head&gt;\r\n	&lt;meta charset=&quot;UTF-8&quot;&gt;\r\n	&lt;title&gt;表单验证&lt;/title&gt;\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n	&lt;center&gt;\r\n	&lt;form action=&quot;111.html&quot; method=&quot;post&quot; onsubmit=&quot;return checkall()&quot;&gt;\r\n		&lt;table&gt;\r\n			&lt;tr&gt;\r\n				&lt;td&gt;用户名&lt;/td&gt;\r\n				&lt;td&gt;&lt;input type=&quot;text&quot; name=&quot;name&quot; onblur=&quot;checkname(this.value)&quot;&gt;&lt;/td&gt;\r\n				&lt;td&gt;\r\n					&lt;span class=&quot;nameinfo&quot;&gt;\r\n					&lt;/span&gt;\r\n				&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n			&lt;tr&gt;\r\n				&lt;td&gt;密码&lt;/td&gt;\r\n				&lt;td&gt;&lt;input type=&quot;password&quot; name=&quot;password&quot; onblur=&quot;checkpwd(this.value)&quot;&gt;&lt;/td&gt;\r\n				&lt;td&gt;&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n			&lt;tr&gt;\r\n				&lt;td&gt;确认密码&lt;/td&gt;\r\n				&lt;td&gt;&lt;input type=&quot;password&quot; name=&quot;repassword&quot; onblur=&quot;checkrepwd(this.value)&quot;&gt;&lt;/td&gt;\r\n				&lt;td&gt;\r\n					&lt;span class=&quot;repwdinfo&quot;&gt;\r\n					&lt;/span&gt;\r\n				&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n			&lt;tr&gt;\r\n				&lt;td&gt;手机号&lt;/td&gt;\r\n				&lt;td&gt;&lt;input type=&quot;text&quot; name=&quot;tel&quot; onblur=&quot;checktel(this.value)&quot;&gt;&lt;/td&gt;\r\n				&lt;td&gt;\r\n					&lt;span class=&quot;telinfo&quot;&gt;\r\n					&lt;/span&gt;\r\n				&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n			&lt;tr&gt;\r\n				&lt;td&gt;邮箱&lt;/td&gt;\r\n				&lt;td&gt;&lt;input type=&quot;text&quot; name=&quot;email&quot; onblur=&quot;checkemail(this.value)&quot;&gt;&lt;/td&gt;\r\n				&lt;td&gt;\r\n					&lt;span class=&quot;emailinfo&quot;&gt;\r\n					&lt;/span&gt;\r\n				&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n			&lt;tr&gt;\r\n				&lt;td colspan=&quot;3&quot;&gt;\r\n					&lt;input type=&quot;submit&quot; value=&quot;注册&quot;&gt;\r\n				&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n		&lt;/table&gt;\r\n	&lt;/form&gt;\r\n	&lt;/center&gt;\r\n	&lt;script type=&quot;text/javascript&quot;&gt;\r\n		//用户名校验\r\n		function checkname(namevalue){\r\n			if(namevalue.length&lt;2||namevalue.length&gt;20){\r\n				document.getElementsByClassName(\'nameinfo\')[0].innerHTML=&quot;&lt;b style=\'color:red\'&gt;请输入2-20位的用户名&lt;/b&gt;&quot;;\r\n				return false;\r\n			}else{\r\n				document.getElementsByClassName(\'nameinfo\')[0].innerHTML=&quot;&lt;b style=\'color:green\'&gt;用户名合法&lt;/b&gt;&quot;;\r\n				return true;\r\n			}\r\n		}\r\n		//密码校验\r\n		function checkpwd(pwdvalue){\r\n			if(pwdvalue.length&lt;2||pwdvalue.length&gt;20){\r\n				document.getElementsByClassName(\'pwdinfo\')[0].innerHTML=&quot;&lt;b style=\'color:red\'&gt;请输入6-16位的密码&lt;/b&gt;&quot;;\r\n				return false;\r\n			}else{\r\n				document.getElementsByClassName(\'pwdinfo\')[0].innerHTML=&quot;&lt;b style=\'color:green\'&gt;密码格式正确&lt;/b&gt;&quot;;\r\n				return true;\r\n			}\r\n		}\r\n		//确认密码校验\r\n		function checkrepwd(repwdvalue){\r\n			if(repwdvalue!=document.getElementsByName(\'password\')[0].value){\r\n				document.getElementsByClassName(\'repwdinfo\')[0].innerHTML=&quot;&lt;b style=\'color:red\'&gt;两次密码输入不一致&lt;/b&gt;&quot;;\r\n				return false;\r\n			}else{\r\n				document.getElementsByClassName(\'repwdinfo\')[0].innerHTML=&quot;&quot;;\r\n				return true;\r\n			}\r\n		}\r\n		//手机校验\r\n		function checktel(telvalue){\r\n			var reg=/^1[3,5,7,8]\\d{9}$/;\r\n			if(!telvalue.match(reg)){\r\n				document.getElementsByClassName(\'telinfo\')[0].innerHTML=&quot;&lt;b style=\'color:red\'&gt;不是中国大陆的手机号&lt;/b&gt;&quot;;\r\n				return false;\r\n			}else{\r\n				document.getElementsByClassName(\'telinfo\')[0].innerHTML=&quot;&quot;;\r\n				return true;\r\n			}\r\n		}\r\n		//邮箱校验\r\n		function checkemail(emailvalue){\r\n			var regemail=/^[0-9a-zA-Z]+@[0-9a-zA-Z]+(.[a-z]{2,9}){1,2}$/;\r\n			if(emailvalue.search(regemail)==-1){\r\n				document.getElementsByClassName(\'emailinfo\')[0].innerHTML=&quot;&lt;b style=\'color:red\'&gt;不正确的邮箱格式&lt;/b&gt;&quot;;\r\n				return false;\r\n			}else{\r\n				document.getElementsByClassName(\'emailinfo\')[0].innerHTML=&quot;&quot;;\r\n				return true;\r\n			}\r\n		}\r\n		//提交校验\r\n		function checkall(){\r\n			var name=document.getElementsByName(\'name\')[0].value;\r\n\r\n			var pwdvalue=document.getElementsByName(\'password\')[0].value;\r\n\r\n			var repwdvalue=document.getElementsByName(\'repassword\')[0].value;\r\n\r\n			var telvalue=document.getElementsByName(\'tel\')[0].value;\r\n\r\n			var emailvalue=document.getElementsByName(\'email\')[0].value;\r\n\r\n			if(checkname(name)&amp;&amp;checkpwd(pwdvalue)&amp;&amp;checkrepwd(repwdvalue)&amp;&amp;checktel(telvalue)&amp;&amp;checkemail(emailvalue)){\r\n				return true;\r\n			}else{\r\n				return false;\r\n			}\r\n		}\r\n	&lt;/script&gt;\r\n&lt;/body&gt;\r\n&lt;/html&gt;\r\n\r\n静态页面：\r\nhtml页面文件：\r\n&lt;!DOCTYPE html&gt;\r\n&lt;html lang=&quot;en&quot;&gt;\r\n&lt;head&gt;\r\n	&lt;meta charset=&quot;UTF-8&quot;&gt;\r\n	&lt;title&gt;静态验证&lt;/title&gt;\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n	&lt;form action=&quot;register.php&quot; method=&quot;post&quot;&gt;\r\n	&lt;table border=&quot;1px&quot;  cellspacing=&quot;0&quot; cellpadding=&quot;0&quot; align=&quot;center&quot;&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;用户名:&lt;/td&gt;\r\n			&lt;td&gt;&lt;input type=&quot;text&quot; name=&quot;username&quot; placeholder=&quot;以a开头&quot;&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;密码:&lt;/td&gt;\r\n			&lt;td&gt;&lt;input type=&quot;password&quot; name=&quot;password&quot; placeholder=&quot;不能纯数字&quot;&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;确认密码:&lt;/td&gt;\r\n			&lt;td&gt;&lt;input type=&quot;password&quot; name=&quot;password&quot; placeholder=&quot;不能纯数字&quot;&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;手机号:&lt;/td&gt;\r\n			&lt;td&gt;&lt;input type=&quot;text&quot; name=&quot;phonenumber&quot;&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;邮箱:&lt;/td&gt;\r\n			&lt;td&gt;&lt;input type=&quot;text&quot; name=&quot;email&quot;&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;&lt;input type=&quot;reset&quot; value=&quot;重置&quot;&gt;&lt;/td&gt;\r\n			&lt;td&gt;&lt;input type=&quot;submit&quot; value=&quot;提交&quot; &gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/table&gt;	\r\n	&lt;/form&gt;	\r\n&lt;/body&gt;\r\n&lt;/html&gt;\r\n\r\nphp页面文件：\r\n&lt;?php\r\n	header(\'content-type:text/html;charset=utf-8\');\r\n	\r\n	if(isset($_POST[\'username\'])&amp;&amp;!empty($_POST[\'username\'])&amp;&amp;isset($_POST[\'password\'])&amp;&amp;!empty($_POST[\'password\'])&amp;&amp;isset($_POST[\'phonenumber\'])&amp;&amp;!empty($_POST[\'phonenumber\'])&amp;&amp;isset($_POST[\'email\'])&amp;&amp;!empty($_POST[\'email\']))\r\n	{\r\n		$username = $_POST[\'username\'];\r\n		$password = $_POST[\'password\'];\r\n		$phonenumber = $_POST[\'phonenumber\'];\r\n		$email=$_POST[\'email\'];\r\n\r\n		$pattern=&quot;/^a\\w{15,31}$/&quot;;\r\n		if(preg_match($pattern,$username))\r\n		{\r\n			echo \'用户名正确\';	exit;\r\n		}\r\n		else\r\n		{\r\n			echo \'用户名不正确\';\r\n		}\r\n\r\n		$pattern1=&quot;/.*\\D.*/&quot;;\r\n\r\n		if(preg_match($pattern1,$password))\r\n		{\r\n			echo \'密码正确\';\r\n		}\r\n		else\r\n		{\r\n			echo \'密码不能纯数字\';\r\n		}\r\n\r\n		$pattern2=&quot;1\\d{10}&quot;;\r\n\r\n		if(preg_match($pattern2,$password))\r\n		{\r\n			echo \'手机号格式正确\';\r\n		}\r\n		else\r\n		{\r\n			echo \'不是中国大陆手机号，请重新输入\';\r\n		}\r\n		$pattern3=&quot;/^([a-zA-z-0-9]{1,20})(([_-.])?([a-zA-z-0-9]{1,20}))*@([a-zA-z-0-9]{1,20})(([-_])?([a-zA-z-0-9]{1,20}))*(.[a-z]{2,4}){1,2}$/&quot;;\r\n\r\n		if(preg_match($pattern3,$email)){\r\n			echo \'邮箱正确\';\r\n		}\r\n		else\r\n		{\r\n			echo \'邮箱不正确\';\r\n		}\r\n\r\n}\r\nelse\r\n{\r\n	echo \'用户名或密码不允许为空\';\r\n\r\n}\r\n\r\n\r\n', '0', '2017-10-18 10:27:39', null, '');
