INSERT INTO `uek_evaluate_answer` VALUES ('7370', '273', '479', '1349', '&lt;!DOCTYPE html&gt;\r\n&lt;html lang=&quot;en&quot;&gt;\r\n&lt;head&gt;\r\n	&lt;meta charset=&quot;UTF-8&quot;&gt;\r\n	&lt;title&gt;表单自动验证&lt;/title&gt;\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n	&lt;center&gt;\r\n		&lt;form action=&quot;&quot; method=&quot;post&quot; onsubmit=&quot;return checkall()&quot;&gt;\r\n			&lt;table&gt;\r\n				&lt;tr&gt;\r\n					&lt;td&gt;用户名&lt;/td&gt;\r\n					&lt;td&gt;&lt;input type=&quot;text&quot; name=&quot;name&quot; onblur=&quot;checkname(this.value)&quot;&gt;&lt;/td&gt;\r\n					&lt;td&gt;\r\n						&lt;span class=&quot;nameinfo&quot;&gt;&lt;/span&gt;\r\n					&lt;/td&gt;\r\n				&lt;/tr&gt;\r\n				&lt;tr&gt;\r\n					&lt;td&gt;密码&lt;/td&gt;\r\n					&lt;td&gt;&lt;input type=&quot;password&quot; name=&quot;password&quot; onblur=&quot;checkpass(this.value)&quot;&gt;&lt;/td&gt;\r\n					&lt;td&gt;\r\n						&lt;span class=&quot;passinfo&quot;&gt;&lt;/span&gt;\r\n					&lt;/td&gt;\r\n				&lt;/tr&gt;\r\n				&lt;tr&gt;\r\n					&lt;td&gt;确认密码&lt;/td&gt;\r\n					&lt;td&gt;&lt;input type=&quot;password&quot; name=&quot;repassword&quot; onblur=&quot;checkrepwd(this.value)&quot;&gt;&lt;/td&gt;\r\n					&lt;td&gt;\r\n						&lt;span class=&quot;repwdinfo&quot;&gt;&lt;/span&gt;\r\n					&lt;/td&gt;\r\n				&lt;/tr&gt;\r\n				&lt;tr&gt;\r\n					&lt;td&gt;手机号&lt;/td&gt;\r\n					&lt;td&gt;&lt;input type=&quot;text&quot; name=&quot;tel&quot; onblur=&quot;checktel(this.value)&quot;&gt;&lt;/td&gt;\r\n					&lt;td&gt;\r\n						&lt;span class=&quot;telinfo&quot;&gt;&lt;/span&gt;\r\n					&lt;/td&gt;\r\n				&lt;/tr&gt;\r\n				&lt;tr&gt;\r\n					&lt;td&gt;邮箱&lt;/td&gt;\r\n					&lt;td&gt;&lt;input type=&quot;text&quot; name=&quot;email&quot; onblur=&quot;checkemail(this.value)&quot;&gt;&lt;/td&gt;\r\n					&lt;td&gt;\r\n						&lt;span class=&quot;emailinfo&quot;&gt;&lt;/span&gt;\r\n					&lt;/td&gt;\r\n				&lt;/tr&gt;\r\n				&lt;tr&gt;\r\n					&lt;td colspan=&quot;3&quot;&gt;&lt;input type=&quot;submit&quot; value=&quot;提交&quot; &gt;&lt;/td&gt;\r\n				&lt;/tr&gt;\r\n			&lt;/table&gt;\r\n		&lt;/form&gt;\r\n	&lt;/center&gt;\r\n	&lt;script type=&quot;text/javascript&quot;&gt;\r\n		function checkname(namevalue){\r\n			if(namevalue.length&lt;2||namevalue.length&gt;10){\r\n				document.getElementsByClassName(\'nameinfo\')[0].innerHTML=&quot;&lt;span style=\'color:red;\'&gt;请输入2-10位的用户名&lt;/span&gt;&quot;;\r\n				return false;\r\n			}else{\r\n				document.getElementsByClassName(\'nameinfo\')[0].innerHTML=&quot;&lt;b style=\'color:green;\'&gt;✔&lt;/b&gt;&quot;;\r\n				return true;\r\n			}\r\n		}\r\n		//密码验证\r\n		function checkpass(passvalue){\r\n			if(passvalue.length&lt;6||passvalue.length&gt;12){\r\n				document.getElementsByClassName(\'passinfo\')[0].innerHTML=&quot;请输入6-12位密码&quot;;\r\n				return false;\r\n			}else{\r\n				document.getElementsByClassName(\'passinfo\')[0].innerHTML=&quot;✔&quot;;\r\n				return true;\r\n			}\r\n		}\r\n		//确认密码\r\n		function checkrepwd(repwdvalue){\r\n			if(repwdvalue!=document.getElementsByName(\'password\')[0].value){\r\n				document.getElementsByClassName(\'repwdinfo\')[0].innerHTML=&quot;&lt;b style=\'color:red;\'&gt;两次密码输入不一致&lt;/b&gt;&quot;;\r\n				return false;\r\n			}else{\r\n				document.getElementsByClassName(\'repwdinfo\')[0].innerHTML=&quot;✔&quot;;\r\n				return true;\r\n			}\r\n		}\r\n		//校验手机号\r\n		function checktel(telvalue){\r\n			var reg=/^1[3,5,7,8][0-9]{9}$/;\r\n				if(telvalue.search(reg)&gt;-1){\r\n					document.getElementsByClassName(\'telinfo\')[0].innerHTML=&quot;&lt;b style=\'color:green;\'&gt;✔&lt;/b&gt;&quot;;\r\n					return true;\r\n				}else{\r\n					document.getElementsByClassName(\'telinfo\')[0].innerHTML=&quot;&lt;b style=\'color:red;\'&gt;不属于大陆手机号&lt;/b&gt;&quot;;\r\n					return false;\r\n				}\r\n		}\r\n		//校验邮箱\r\n		function checkemail(emailvalue){\r\n			//995222109@qq.com\r\n			var reg = /^([a-zA-Z0-9_-])+@([a-zA-Z0-9_-])+(\\.[a-zA-Z0-9_-])+/;\r\n			if(emailvalue.match(reg)){\r\n				document.getElementsByClassName(\'emailinfo\')[0].innerHTML=&quot;&lt;b style=\'color:green;\'&gt;✔&lt;/b&gt;&quot;;\r\n				return true;\r\n			}else{\r\n				document.getElementsByClassName(\'emailinfo\')[0].innerHTML=&quot;&lt;b style=\'color:red;\'&gt;请重新输入邮箱&lt;/b&gt;&quot;;\r\n				return false;\r\n			}\r\n		}\r\n		//提交校验\r\n		function checkall(){\r\n			var name=document.getElementsByName(\'name\')[0].value;\r\n			var passvalue=document.getElementsByName(\'password\')[0].value;\r\n			var repwdvalue=document.getElementsByName(\'repassword\')[0].value;\r\n			var telvalue=document.getElementsByName(\'tel\')[0].value;\r\n			var emailvalue=document.getElementsByName(\'email\')[0].value;\r\n			if(checkname(name)&amp;&amp;checkpass(passvalue)&amp;&amp;checkrepwd(repwdvalue)&amp;&amp;checktel(telvalue)&amp;&amp;checkemail(emailvalue)){\r\n				return true;\r\n			}else{\r\n				return false;\r\n			}\r\n		}\r\n	&lt;/script&gt;\r\n&lt;/body&gt;\r\n&lt;/html&gt;', '0', '2017-10-18 10:40:39', null, '');
