n			if(namevalue.length&lt;2 || namevalue.length&gt;5){\r\n				// alert(\'11\')\r\n				document.getElementsByName(\'namemessage\')[0].innerHTML=&quot;请输入2-5位的用户名&quot;;\r\n				return false;\r\n			}else{\r\n				document.getElementsByName(\'namemessage\')[0].innerHTML=&quot;用户名合法&quot;;\r\n				return true;\r\n			}\r\n		}\r\n		// 验证密码\r\n		function writepassword(pasvalue){\r\n			if(pasvalue.length&lt;2 || pasvalue.length&gt;5){\r\n				// alert(\'11\')\r\n				document.getElementsByName(\'pasmessage\')[0].innerHTML=&quot;请输入2-5位密码&quot;;\r\n				return false;\r\n			}else{\r\n				document.getElementsByName(\'pasmessage\')[0].innerHTML=&quot;用户名合法&quot;;\r\n				return true;\r\n			}\r\n		}\r\n		//确认密码校验\r\n		function checkrepwd(repwdvalue){\r\n			if(repwdvalue!=document.getElementsByName(\'password\')[0].value){\r\n				document.getElementsByName(\'repasmessage\')[0].innerHTML=&quot;两次密码输入不一致&quot;;\r\n				return false;\r\n			}else{\r\n				document.getElementsByName(\'repasmessage\')[0].innerHTML=&quot;&quot;;\r\n				return ture;\r\n			}\r\n		}\r\n		//手机校验\r\n		function checktel(telvalue){\r\n			var reg=/^1[3,5,7,8]\\d{9}$/;\r\n					if(!reg.exec(telvalue)){\r\n				document.getElementsByName(\'telmessage\')[0].innerHTML=&quot;不是中国大陆的手机号&quot;;\r\n				return false;\r\n			}else{\r\n				document.getElementsByName(\'telmessage\')[0].innerHTML=&quot;&quot;;\r\n				return ture;\r\n			}\r\n		}\r\n		//邮箱校验\r\n		function checkemail(emailvalue){\r\n			var regemail=/^([a-zA-Z0-9_-])+@([a-zA-Z0-9_-])+((\\.[a-zA-Z0-9_-]{2,3}){1,2})$/;\r\n			if(emailvalue.search(regemail)==-1){\r\n				document.getElementsByName(\'emailmessage\')[0].innerHTML=&quot;不正确的邮箱格式&quot;;\r\n				return false;\r\n			}else{\r\n				document.getElementsByName(\'emailmessage\')[0].innerHTML=&quot;&quot;;\r\n				return ture;\r\n			}\r\n		}\r\n		\r\n	&lt;/script&gt;\r\n\r\n\r\n&lt;/body&gt;\r\n&lt;/html&gt;', '0', '2017-10-18 10:42:04', null, '');
