INSERT INTO `uek_evaluate_recruit_titles` VALUES ('21', '01010201040021', '如果子元素设置了浮动，而父元素没有设置高度的话，父元素不会被自动撑开，有什么解决办法（）\nA 没有解决办法，只能给父元素设置高度\nB 给子元素设置overflow：hidden\nC 给父元素设置overflow：hidden\nD 给该子元素设置clear：both', '0', '1', 'C', '', '10', '2016-10-25 18:28:56', '2', '3', '0', '1', '1', '2', '4', null, null, '1', null);
