INSERT INTO `uek_evaluate_recruit_titles` VALUES ('152', '01010201045057', '字符串怎么转成整数，有几种方法？怎么实现？', '6', '1', '1) 强制类型转换: (整型)字符串变量名;\n2) 直接转换：settype(字符串变量,整型);\n3) intval(字符串变量);', '', '10', '2017-01-15 14:49:09', '5', '5', '0', '1', '1', '7', '16', null, null, '1', null);
