INSERT INTO `uek_evaluate_recruit_titles` VALUES ('154', '01010201045059', '如何在一个 php  函数中使用具有全局属性的变量，说出两种方式？', '6', '1', 'global $a;\n$GLOBALS[\'a\']', '', '10', '2017-01-15 14:49:09', '5', '5', '0', '1', '1', '7', '16', null, null, '1', null);
INSERT INTO `uek_evaluate_recruit_titles` VALUES ('155', '01010201045060', 'PHP 中的注释的写法，要列出三种？', '6', '1', '// 注释内容 C 风格的单行注释\n# 注释内容 unix 风格的单行注释\n/* 注释内容 */ C 风格的多行注释', '', '10', '2017-01-15 14:49:09', '10', '5', '0', '1', '1', '7', '13', null, null, '1', null);
INSERT INTO `uek_evaluate_recruit_titles` VALUES ('156', '01010201045061', '函数中的默认参数位置如何放置？', '6', '1', '当使用默认参数时，任何默认参数必须放在任何非默认参数的右侧；否则，函数将不会按照预期的情况工作。', '', '10', '2017-01-15 14:49:09', '5', '5', '0', '1', '1', '7', '16', null, null, '1', null);
