INSERT INTO `uek_evaluate_recruit_titles` VALUES ('722', '01020301064001', '利用过滤选择器实现隔行换色效果', '4', '', '&lt;!DOCTYPE&gt;\n&lt;html&gt;\n    &lt;head&gt;\n        &lt;title&gt;隔行换色&lt;/title&gt;\n        &lt;meta charset=&quot;utf-8&quot; /&gt;\n        \n        &lt;script type=&quot;text/javascript&quot; src=&quot;../jquery-1.4.4.js&quot;&gt;&lt;/script&gt;\n        &lt;script type=&quot;text/javascript&quot;&gt;\n        //页面加载，设置行的颜色\n        window.onload = function(){\n            $(&quot;tr:first&quot;).css(\'background-color\',\'lightgreen\');\n            //偶数除去第一个\n            //$(&quot;tr:even:not(:first)&quot;).css(\'background-color\',\'lightblue\');\n            //:gt()大于给定索引的值开始，从0开始\n            $(&quot;tr:gt(0):odd&quot;).css(\'background-color\',\'lightblue\');\n        }\n        &lt;/script&gt;\n        &lt;style type=&quot;text/css&quot;&gt;\n        table {\n   width:500px;\n   border:1px solid black;\n   margin:auto; \n  }\n        td {\n   border:1px solid black;\n  }\n        &lt;/style&gt;\n    &lt;/head&gt;\n    &lt;body&gt;\n       \n        &lt;table style=&quot;height:100%;width:100%&quot;&gt;\n   &lt;tr&gt;\n    &lt;td&gt;序号&lt;/td&gt;&lt;td&gt;姓名&lt;/td&gt;&lt;td&gt;年龄&lt;/td&gt;&lt;td&gt;性别&lt;/td&gt;\n   &lt;/tr&gt;\n   &lt;tr&gt;\n    &lt;td&gt;1&lt;/td&gt;&lt;td&gt;王小二&lt;/td&gt;&lt;td&gt;12&lt;/td&gt;&lt;td&gt;男&lt;/td&gt;\n   &lt;/tr&gt;\n   &lt;tr&gt;\n    &lt;td&gt;2&lt;/td&gt;&lt;td&gt;张三&lt;/td&gt;&lt;td&gt;44&lt;/td&gt;&lt;td&gt;男&lt;/td&gt;\n   &lt;/tr&gt;\n   &lt;tr&gt;\n    &lt;td&gt;3&lt;/td&gt;&lt;td&gt;李四&lt;/td&gt;&lt;td&gt;23&lt;/td&gt;&lt;td&gt;女&lt;/td&gt;\n   &lt;/tr&gt;\n   &lt;tr&gt;\n    &lt;td&gt;4&lt;/td&gt;&lt;td&gt;王五&lt;/td&gt;&lt;td&gt;35&lt;/td&gt;&lt;td&gt;男&lt;/td&gt;\n   &lt;/tr&gt;\n   &lt;tr&gt;\n    &lt;td&gt;5&lt;/td&gt;&lt;td&gt;赵六&lt;/td&gt;&lt;td&gt;26&lt;/td&gt;&lt;td&gt;男&lt;/td&gt;\n   &lt;/tr&gt;\n        &lt;/table&gt;\n    &lt;/body&gt;\n&lt;/html&gt;', '', '10', '2016-12-17 16:32:12', '10', '0', '0', '1', '2', '13', '36', null, null, '1', null);
INSERT INTO `uek_evaluate_recruit_titles` VALUES ('723', '01020301064002', '利用样式操作(height()、width())设置动画效果：点击放大按钮放大，点击缩小按钮缩小', '4', '', '&lt;!DOCTYPE&gt;\n&lt;html&gt;\n    &lt;head&gt;\n        &lt;title&gt;新建网页&lt;/title&gt;\n        &lt;meta charset=&quot;utf-8&quot; /&gt;\n        &lt;script type=&quot;text/javascript&quot; src=&quot;../jquery-1.11.2.js&quot;&gt;&lt;/script&gt;\n        &lt;script type=&quot;text/javascript&quot;&gt;\n        function big(){\n            //获得图片原宽度、高度\n            var old_w = $(\'img\').width();\n            var old_h = $(\'img\').height();\n   //console.log(old_w);\n            //增大动画效果，每次给宽度增加5px\n            var new_w = old_w+5;\n            var new_h = old_h+5;\n            //给img设置新的宽度和高度\n            $(\'img\').attr(\'width\',new_w);\n            $(\'img\').attr(\'height\',new_h);\n        }\n        function small(){\n            //获得图片原宽度、高度\n            var old_w = $(\'img\').width();\n            var old_h = $(\'img\').height();\n            //增大动画效果，每次给宽度减少5px\n            var new_w = old_w-5;\n            var new_h = old_h-5;  \n            if(new_w ==140){return;}\n            //给img设置新的宽度和高度\n            $(\'img\').width(new_w);\n            $(\'img\').height(new_h);\n        }\n        &lt;/script&gt;\n    &lt;/head&gt;\n    &lt;body&gt;\n        &lt;img style=&quot;display:block;margin:0 auto;&quot; src=&quot;1.jpg&quot; alt=&quot;&quot; width=&quot;200&quot; height=&quot;100&quot;/&gt;\n        &lt;br /&gt;&lt;br /&gt;\n        &lt;input type=&quot;button&quot; value=&quot;放大&quot;  onclick=&quot;big()&quot; /&gt;\n        &lt;input type=&quot;button&quot; value=&quot;缩小&quot;  onclick=&quot;small()&quot; /&gt;\n    &lt;/body&gt;\n&lt;/html&gt;', '', '10', '2016-12-17 16:32:12', '10', '0', '0', '1', '2', '13', '36', null, null, '1', null);
INSERT INTO `uek_evaluate_recruit_titles` VALUES ('724', '01020301064003', '两个下拉列表项目切换（dom节点操作）：\n实现功能：1.点击 -&gt; 把左边选中的移动到右边 2.点击==&gt;把左边全部移动到右边 3.双击左边或者右边的某个选项，将其自动移动到另外一边 ', '4', '', '&lt;!DOCTYPE&gt;\n&lt;html&gt;\n&lt;head&gt;\n &lt;meta http-equiv=&quot;Content-Type&quot; content=&quot;text/html;charset=ANSI&quot; /&gt;\n &lt;title&gt;网页标题&lt;/title&gt;\n &lt;style type=&quot;text/css&quot;&gt;\n  h2{margin:auto;text-align:center;}\n .main{width:600px;height:400px;background-color:#0099cc;margin:0 auto;}\n .content{width:450px;height:250px;background-color:lightgray;position:absolute;margin:35px; margin-left:90px;margin-top:50px;}\n .area1 select{width:120px;height:200px;background-color:#ccff66;border:1px solid #000000;margin-left:60px;margin-top:20px; float:left;}\n .content span{display:block;text-align:center;margin-top:4px;}\n .content option{text-align:center;margin-top:4px;font-size:17px;}\n .area2{background-color:#ccff66; border:1px dashed #000000;margin-left:20px;margin-top:20px;}\n .area2 span{color:red;font-size:25px;font-weight:bold;}\n .area3 select{width:120px;height:200px;background-color:#ccff66;border:1px solid #000000;margin-left:20px;margin-top:20px;float:left}\n &lt;/style&gt;\n &lt;script type=&quot;text/javascript&quot; src=&quot;jquery-1.4.4.js&quot;&gt;&lt;/script&gt;\n &lt;script type=&quot;text/javascript&quot;&gt;\n  //从左向右追加 从右向左追加\n  function f1(num){\n   if(num==1){\n    $(\'#rightmenu\').append($(\'#leftmenu option:selected\'));\n   }else(\n    $(\'#leftmenu\').append($(\'#rightmenu option:selected\'));\n   )\n  } \n  //全部追加过去\n  function f2(nm){\n  \n   if(nm==1){\n    $(\'#rightmenu\').append($(\'#leftmenu option\'));\n   }else(\n    $(\'#leftmenu\').append($(\'#rightmenu option\'));\n   )\n  }\n  //双击追加 在某个元素上双击的时候就会触发dblclick事件\n  $(function(){\n   $(\'#leftmenu\').dblclick(function(){\n    $(\'#rightmenu\').append($(\'#leftmenu option:selected\'));\n   })\n   $(\'#rightmenu\').dblclick(function(){\n    $(\'#leftmenu\').append($(\'#rightmenu option:selected\'));\n   })\n  \n  })\n  \n &lt;/script&gt;\n&lt;/head&gt;\n&lt;body&gt;\n  &lt;h2&gt;下拉列表项目切换&lt;/h2&gt;\n  &lt;div class=\'main\'&gt;\n   &lt;div class=\'content\'&gt;\n    &lt;div class=\'area1\' &gt;\n     &lt;select multiple=&quot;multiple&quot; id=\'leftmenu\'&gt;\n      &lt;option&gt;用户管理&lt;/option&gt;\n      &lt;option&gt;菜单管理&lt;/option&gt;\n      &lt;option&gt;角色管理&lt;/option&gt;\n      &lt;option&gt;店铺管理&lt;/option&gt;\n      &lt;option&gt;商品管理&lt;/option&gt;\n     &lt;/select&gt;\n    &lt;/div&gt;\n    &lt;div class=\'area2\'&gt;\n      &lt;span onclick=\'f1(1)\'&gt;--&gt;&lt;/span&gt;\n      &lt;span onclick=\'f1(2)\'&gt;&lt;--&lt;/span&gt;\n      &lt;span onclick=\'f2(1)\'&gt;==&gt;&lt;/span&gt;\n      &lt;span onclick=\'f2()\'&gt;&lt;==&lt;/span&gt;\n    &lt;/div&gt;\n    &lt;div class=\'area3\'&gt;\n     &lt;select multiple=&quot;multiple&quot; id=\'rightmenu\'&gt;\n      &lt;option&gt;邮件管理&lt;/option&gt;\n      &lt;option&gt;地址管理&lt;/option&gt;\n      &lt;option&gt;订单管理&lt;/option&gt;\n      &lt;option&gt;公告管理&lt;/option&gt;\n      &lt;option id=\'gifts\'&gt;礼品管理&lt;/option&gt;\n     &lt;/select&gt;\n    &lt;/div&gt;\n   &lt;/div&gt;\n  &lt;/div&gt;\n&lt;/body&gt;\n&lt;/html&gt;', '', '10', '2016-12-17 16:32:12', '10', '0', '0', '1', '2', '13', '37', null, null, '1', null);
INSERT INTO `uek_evaluate_recruit_titles` VALUES ('725', '01020301021001', 'body中的onload()函数和jQuery中的document.ready()有什么区别？A、我们可以在页面中使用多个document.ready()，但只能使用一次onload()。\nB、document.ready()函数在页面DOM元素加载完以后就会被调用，而onload()函数则要在所有的关联资源（包括图像、音频）加载完毕后才会调用。&quot; \nC.我们可以在页面中使用多个onload()，但只能使用一次document.ready() \nD.没区别', '1', null, 'AB', '', '8', '2016-12-18 14:15:41', '1', '1', '0', '1', '2', '13', '36', null, null, '1', null);
