INSERT INTO `uek_evaluate_recruit_titles` VALUES ('1139', '01010902016001', '写一个函数，能够遍历一个文件夹下的所有文件和子文件夹', '6', '2', 'function dir_recurse($dir) {\r\n	  $i = 1; if($handle = opendir($dir)) {\r\n	    while(false !== ($file = readdir($handle))) {\r\n	     if($file != &quot;.&quot; &amp;&amp; $file != &quot;..&quot; ) \r\n	  				{ if(is_dir($dir.&quot;/&quot;.$file) == true){ \r\n	  					 	$fullpath = $dir.&quot;/&quot;.$file; dir_recurse($fullpath); echo &quot;$fullpath\\n&quot;; $i++;\r\n	  			    }else { \r\n	  				   $fullpath = $dir.&quot;/&quot;.$file; \r\n	  				   echo &quot;$fullpath\\n&quot;; $i++; \r\n	  				     } \r\n	  			    }\r\n	  	       } \r\n	  	 closedir($handle);\r\n	  	  }\r\n	  }', '无', null, '2017-08-11 11:19:47', '10', '2', '0', '1', '1', '9', '22', '155', null, '2', null);
