INSERT INTO `uek_evaluate_recruit_titles` VALUES ('1240', '01010904024001', '完成注册模块', '4', '2', '&lt;!DOCTYPE html&gt;\r\n&lt;html lang=&quot;en&quot;&gt;\r\n&lt;head&gt;\r\n	&lt;meta charset=&quot;UTF-8&quot;&gt;\r\n	&lt;title&gt;Document&lt;/title&gt;\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n	&lt;form action=&quot;register.php&quot; method=&quot;post&quot;&gt;\r\n		用户名：&lt;input type=&quot;text&quot; name=&quot;username&quot;&gt;&lt;br&gt;\r\n		密码：&lt;input type=&quot;password&quot; name=&quot;password&quot;&gt;&lt;br&gt;\r\n		&lt;input type=&quot;submit&quot; value=&quot;注册&quot;&gt;\r\n	&lt;/form&gt;	\r\n&lt;/body&gt;\r\n&lt;/html&gt;\r\n\r\n&lt;?php\r\n	header(\'content-type:text/html;charset=utf-8\');\r\n	// 接收客户端用户名\r\n	$username = $_POST[\'username\'];\r\n	// 接收客户端用户名\r\n	$password = $_POST[\'password\'];\r\n\r\n	//1. 连接数据库(建立服务器与数据库的桥梁)\r\n	$link = mysql_connect(\'localhost\',\'root\',\'root\');\r\n\r\n	//2.选择数据库\r\n	mysql_select_db(\'db_test\',$link);\r\n	// 3.准备sql\r\n	$sql = &quot;insert into t_user(username,password) value(\'$username\',\'$password\')&quot;;\r\n	//echo $sql;\r\n	\r\n	// 4.执行sql\r\n	$result = mysql_query($sql,$link);\r\n	if($result){\r\n		echo \'注册用户成功\';\r\n	}else{\r\n		echo \'注册用户失败\';\r\n	}\r\n	// 5.关闭数据库\r\n	mysql_close($link);', '&lt;?php\r\n	header(\'content-type:text/html;charset=utf-8\');\r\n	// 接收客户端用户名\r\n	$username = $_POST[\'username\'];\r\n	// 接收客户端用户名\r\n	$password = $_POST[\'password\'];\r\n\r\n	//1. 连接数据库(建立服务器与数据库的桥梁)\r\n	$link = mysql_connect(\'localhost\',\'root\',\'root\');\r\n\r\n	//2.选择数据库\r\n	mysql_select_db(\'db_test\',$link);\r\n	// 3.准备sql\r\n	$sql = &quot;insert into t_user(username,password) value(\'$username\',\'$password\')&quot;;\r\n	//echo $sql;\r\n	\r\n	// 4.执行sql\r\n	$result = mysql_query($sql,$link);\r\n	if($result){\r\n		echo \'注册用户成功\';\r\n	}else{\r\n		echo \'注册用户失败\';\r\n	}\r\n	// 5.关闭数据库\r\n	mysql_close($link);', null, '2017-08-24 21:54:39', '20', '20', '0', '1', '1', '9', '24', null, null, '1', null);
