INSERT INTO `uek_evaluate_titles` VALUES ('173', '01010201044016', '用最少的代码写一个求 3 值最大值的函数.  (51.com  笔试题)', '4', '2', '&lt;?php\nfunction maximum($a,$b,$c){\n    return $a &gt; $b ? ($a &gt; $c ?$a : $c):($b &gt; $c ? $b : $c);\n}\n//调用实例\necho maximum(24, 15, 8);', '', '10', '2016-10-27 14:47:32', '10', '5', '0', '1', '1', '7', '16', null, null, '1', null);
INSERT INTO `uek_evaluate_titles` VALUES ('174', '01010201044017', '不使用第三个变量交换两个变量的值', '4', '2', '&lt;?php\n//方案一：\n$a = \'PHP\';\n$b = \'MySQL\';\necho $a,$b;\nlist($a,$b) = array($b,$a);\necho &quot;&lt;br/&gt;&quot;;\necho $a,$b;\n//方案二：\n//对字符进行编码，比如MD5,sha1,base64_encode,但是要保证编码是可逆的\n$a =&quot;PHP编程&quot;;\n$b = &quot;MySQL数据库&quot;;\necho $a,$b;\n$a = base64_encode($a);\n$b = base64_encode($b);\n$a = $a.\'&amp;\'.$b;//得到一个有&amp;符号连接的字符串\n//使用字符串分割函数得到数组\n$b = explode(\'&amp;\', $a);\n$a = base64_decode($b[1]);//得到第一个字符并解码\n$b = base64_decode($b[0]);//得到第二个字符并解码\n\necho \'&lt;br/&gt;\';\necho $a,$b;', '', '10', '2017-04-17 16:41:19', '10', '5', '0', '1', '1', '7', '16', null, null, '1', null);
INSERT INTO `uek_evaluate_titles` VALUES ('175', '01010201044018', '用 PHP 打印出前一天的时间格式是  2016-5-10  22:21:21 。', '4', '2', '&lt;?php\necho date(&quot;Y-m-d H:i:s&quot;,time()-3600*24);\n//使用strtotime,可以将任何字符串的时间表示转换成时间戳，仅针对英文\necho \'&lt;br/&gt;\';\necho date(\'Y-m-d H:i:s\',strtotime(&quot;-1 day&quot;));\n//在实际开发中，strtotime非常有用，因为对于表单来说，提交的数据都是字符串\n//比如，2016-4-27，需要将其转成时间戳然后存到数据库', '', '10', '2016-10-27 14:47:33', '10', '5', '0', '1', '1', '7', '16', null, null, '1', null);
INSERT INTO `uek_evaluate_titles` VALUES ('176', '01010201044019', '将1234567890  转换成  1,234,567,890 每 3  位用逗号隔开的形式。（百度）', '4', '2', '$str = \'1234567890\';\nfunction str(){\n    //反转字符串得到0987654321\n    $str = strrev($str);\n    //使用，分割字符串，得到098，765，432，1\n    $str = chunk_split($str,3,\',\');\n    //再次反转字符串，得到，1，234，567，890\n    $str = strrev($str);\n    //去掉左边的，得到1，234，567，890\n    $str = ltrim($str,\',\');\n    return $str;\n}', '', '10', '2016-10-27 14:47:33', '10', '10', '0', '1', '1', '7', '16', null, null, '1', null);
