INSERT INTO `uek_evaluate_titles` VALUES ('193', '01010201044036', '以下语句可能存在错误，如果存在错误请指出什么\n错误（每个语句单独考虑）\nprint_r $val = 333;\nprint_r($val = 333);\n$a += ($b = 4) + 5;\n$foo[bar] = \'enemy\'; \nfunction x($a = &quot;1&quot;, $b){} ', '4', '2', 'print_r $val = 333; // 有错，print_r 是一个函数，需加上括号，如果是 print 则可以\nprint_r($val = 333); // 无\n$a += ($b = 4) + 5; // 无\n$foo[bar] = \'enemy\'; // 无，最好 bar 加上引号\nfunction x($a = &quot;1&quot;, $b){} // 无，但给参数默认值应尽量从右向左', '', '10', '2016-10-27 14:47:34', '10', '10', '0', '1', '1', '7', '13', null, null, '1', null);
