INSERT INTO `uek_evaluate_titles` VALUES ('830', '01040101026012', '微信认证后的作用有哪些？', '6', null, '微信认证是微信公众平台为了确保公众帐号的信息的真实性、安全性，目前提供给微信公众服务号进\n行微信认证的服务。\n1、微信认证后，获得更丰富的高级接口，向用户提供更有价值的个性化服务。\n2、微信认证后，用户将在微信中看到微信认证特有的标识。', '', '10', '2017-04-17 16:24:48', '10', null, '0', '1', '4', '19', '58', null, null, '1', null);
INSERT INTO `uek_evaluate_titles` VALUES ('831', '01040101026013', '微信公众平台是什么？', '6', null, '微信公众号主要面向名人、政府、媒体、企业等机构推出的合作推广业务。在这里可以通过微信渠道将\n品牌推广给上亿的微信用户，减少宣传成本，提高品牌知名度，打造更具影响力的品牌形象。', '', '10', '2017-04-17 16:24:49', '10', null, '0', '1', '4', '19', '58', null, null, '1', null);
INSERT INTO `uek_evaluate_titles` VALUES ('832', '01040101020007', '服务号与订阅号有什么区别？( )\nA.发送信息数不一样\nB.手机端显示的界面不一样\nC.支持的支付功能不一样\nD.ABC都是\n\n\n\n\n\n ', '0', null, 'D', '', '10', '2017-04-17 16:24:49', '5', null, '0', '1', '4', '19', '58', null, null, '1', null);
