INSERT INTO `uek_evaluate_titles` VALUES ('866', '01060201036010', '语义化的理解？', '6', '', '用正确的标签做正确的事情！\n\nhtml语义化就是让页面的内容结构化，便于对浏览器、搜索引擎解析；\n\n在没有样式CCS情况下也以一种文档格式显示，并且是容易阅读的。\n\n搜索引擎的爬虫依赖于标记来确定上下文和各个关键字的权重，利于 SEO。\n\n使阅读源代码的人对网站更容易将网站分块，便于阅读维护理解', '', '10', '2017-07-31 19:42:00', '10', '0', '0', null, null, null, null, null, null, '1', null);
