INSERT INTO `uek_evaluate_titles` VALUES ('874', '01010802026001', '什么是重写？什么是重载？', '6', '1', '重写/覆盖  override  \r\n指:子类重写了父类的同名方法  \r\n重载: overload  \r\n重载是指:存在多个同名方法,但参数类型/个数不同.  \r\n传不同的参数,调用不同的方法  \r\n但是在PHP中,不允许存在多个同名方法.  \r\n因此,不能够完成java,c++中的这种重载  \r\n但是,PHP的灵活,能达到类似的效果  ', '重写和重载的意义和区别', null, '2017-08-02 19:37:33', '5', '0', '0', '1', '1', '8', '19', null, null, '1', null);
INSERT INTO `uek_evaluate_titles` VALUES ('875', '01010802014001', '实现一下单例设计模式', '4', '1', '&lt;?php\r\n	//单例模式：只能创建一个对象（至始至终都是一个对象）\r\n	class DanLi{\r\n		static $instance=null;\r\n		private function __construct(){\r\n\r\n		}\r\n		// 创建对象\r\n		static function getInstance(){\r\n			if(self::$instance == null){\r\n				self::$instance = new DanLi();\r\n			}\r\n			return self::$instance;\r\n		}\r\n		//私有化克隆方法\r\n		private function __clone(){\r\n			\r\n		}\r\n	}\r\n	$obj = DanLi::getInstance();\r\n	var_dump($obj);\r\n	DanLi::getInstance();\r\n', '单例', null, '2017-08-02 19:39:26', '10', '5', '0', '1', '1', '8', '19', null, null, '1', null);
INSERT INTO `uek_evaluate_titles` VALUES ('876', '01010802016001', '写出 php 的 public、protected、private 三种访问控制模式的区别（新浪网技术部）', '6', '1', 'public：公有，任何地方都可以访问\r\nprotected：继承，只能在本类或子类中访问，在其它地方不允许访问\r\nprivate：私有，只能在本类中访问，在其他地方不允许访问', '  public、protected、private ', null, '2017-08-02 19:40:25', '10', '5', '0', '1', '1', '8', '19', null, null, '1', null);
