INSERT INTO `uek_evaluate_titles` VALUES ('1046', '01010704096002', '使用五种以上方式获取一个文件的扩展名\r\n	要求：dir/upload.image.jpg，找出 .jpg 或者 jpg ，\r\n	必须使用PHP自带的处理函数进行处理，方法不能明显重复，可以封装成函数', '6', '4', 'get_ext1($file_name), get_ext2($file_name)\r\n	function get_ext1($file_name){\r\n	 return strrchr($file_name, \'.\');\r\n	}\r\n	function get_ext2($file_name){\r\n	 return substr($file_name, strrpos($file_name, \'.\'));\r\n	}\r\n	function get_ext3($file_name){\r\n	 return array_pop(explode(\'.\', $file_name));\r\n	}\r\n	function get_ext4($file_name){\r\n	 $p = pathinfo($file_name);\r\n	 return $p[\'extension\'];\r\n	}\r\n	function get_ext5($file_name){\r\n	 return strrev(substr(strrev($file_name), 0, strpos(strrev($file_name), \'.\')));\r\n	}', '无', null, '2017-08-09 19:23:17', '10', '10', '0', '1', '1', '7', '16', '139', null, '2', null);
