INSERT INTO `uek_evaluate_titles` VALUES ('1127', '01020103016002', '用javascript写一个函数,其功能是可以删除数组中重复的元素.', '6', '2', 'function myArray_Unique(Array)\r\n{\r\n var haha=Array;\r\n for(var i=0;i&lt;Array.length;i++)\r\n {\r\n  for(var j=0;j&lt;Array.length;j++)\r\n  {\r\n   temp=Array[i];\r\n   if((i+j+1)&lt;Array.length&amp;&amp;temp==Array[i+j+1]) //如果当前元素与后一个元素相等\r\n    haha.splice(i+j+1,1);       //然后就移除下一个元素   \r\n  }\r\n }\r\n return haha;\r\n}', '无', null, '2017-08-11 09:23:30', '5', '5', '0', '1', '2', '11', '32', '154', null, '2', null);
