INSERT INTO `uek_evaluate_titles` VALUES ('1189', '01020102014006', '在下面的HTML文档中，编写函数test() ,实现如下功能： \n（1）当多行文本框中的字符数超过20个，截取至20个  \n（2）在id为number的td中（第一行第二列）显示文本框的字符个数', '4', '1', '<html>     <head> \n        <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />         <title>留言</title> \n        <script> \n       function test(){ \n              var content = document.getElementById(\"feedBack\").value; \n\n              if(content.length>20){ \n                     content = content.substr(0,20);               } \n              document.getElementById(\"feedBack\").value=content; \n              document.getElementById(\"number\").innerHTML=content.length;        } </script>     </head>     <body>         <table>             <tr>                 <td>                     留言 \n                </td> \n                <td id=\"number\">                     0                 </td>             </tr>             <tr> \n                <td colspan=2> \n                    <textarea id=\"feedBack\" onkeyup=\"test()\" rows=6></textarea>                 </td>             </tr>         </table>     </body> </html> \n \n\n\n', '', '10', '2017-08-19 14:25:28', '10', '5', '0', '1', '2', '11', '31', null, null, '1', null);
INSERT INTO `uek_evaluate_titles` VALUES ('1190', '01020102014007', '实现单例设计模式', '4', '2', '// 单体模式\nvar Singleton = function(name){\n    this.name = name;\n    this.instance = null;\n};\nSingleton.prototype.getName = function(){\n    return this.name;\n}\n// 获取实例对象\nfunction getInstance(name) {\n    if(!this.instance) {\n        this.instance = new Singleton(name);\n    }\n    return this.instance;\n}\n// 测试单体模式的实例\nvar a = getInstance(\"aa\");\nvar b = getInstance(\"bb\");', '', '10', '2017-08-19 14:25:28', '10', null, '0', '1', '2', '11', '33', null, null, '1', null);
